/* ============================================
 * Font Rendering Functions
 * ============================================ */
#include <kernel/graphics.h>

/*
 * Draw single character using TTF-rendered bitmap
 */
void graphics_draw_char(uint32_t x, uint32_t y, char c, uint32_t color) {
  struct graphics_context *ctx = graphics_get_context();
  if (!ctx || !ctx->buffer)
    return;

  int idx = (int)c - FONT_FIRST_CHAR;
  if (idx < 0 || idx >= FONT_NUM_CHARS)
    return;

  const struct glyph_info *g = &font_glyphs[idx];
  if (g->width == 0 || g->height == 0)
    return;

  const uint8_t *bitmap = &font_bitmap[g->data_offset];

  /* Draw each pixel of the glyph */
  for (int gy = 0; gy < g->height; gy++) {
    for (int gx = 0; gx < g->width; gx++) {
      uint8_t alpha = bitmap[gy * g->width + gx];
      if (alpha == 0)
        continue;

      int px = (int)x + g->x0 + gx;
      int py = (int)y + g->y0 + gy + FONT_ASCENT;

      if (px >= 0 && (uint32_t)px < ctx->width && py >= 0 &&
          (uint32_t)py < ctx->height) {

        if (alpha >= 255) {
          ctx->buffer[py * ctx->width + px] = color;
        } else {
          /* Alpha blend */
          uint32_t bg = ctx->buffer[py * ctx->width + px];
          uint32_t inv = 255 - alpha;

          uint32_t r =
              (((color >> 16) & 0xFF) * alpha + ((bg >> 16) & 0xFF) * inv) /
              255;
          uint32_t gr =
              (((color >> 8) & 0xFF) * alpha + ((bg >> 8) & 0xFF) * inv) / 255;
          uint32_t b = ((color & 0xFF) * alpha + (bg & 0xFF) * inv) / 255;

          ctx->buffer[py * ctx->width + px] =
              0xFF000000 | (r << 16) | (gr << 8) | b;
        }
      }
    }
  }
}

/*
 * Get character advance width
 */
int graphics_char_width(char c) {
  int idx = (int)c - FONT_FIRST_CHAR;
  if (idx < 0 || idx >= FONT_NUM_CHARS)
    return 0;
  return font_glyphs[idx].advance;
}

/*
 * Draw string using TTF-rendered bitmap font
 */
void graphics_draw_string(uint32_t x, uint32_t y, const char *str,
                          uint32_t color) {
  if (!str)
    return;

  int cur_x = (int)x;

  while (*str) {
    graphics_draw_char(cur_x, y, *str, color);
    cur_x += graphics_char_width(*str);
    str++;
  }
}

/*
 * Get string width in pixels
 */
int graphics_string_width(const char *str) {
  if (!str)
    return 0;

  int width = 0;
  while (*str) {
    width += graphics_char_width(*str);
    str++;
  }
  return width;
}

/*
 * Get font height
 */
int graphics_font_height(void) { return FONT_SIZE; }

/*
 * Get font ascent
 */
int graphics_font_ascent(void) { return FONT_ASCENT; }
